<script lang="ts">
    import {dark_theme} from "@src/store/store"
    
    let html =globalThis.document?.getElementsByTagName("html")[0]
$:{$dark_theme?
    html?.classList.add('dark_theme') :
	html?.classList.remove('dark_theme')
}
</script>

<style>
    svg{
        position: absolute;
        left:10px;
        top:10px;
        
    }
   
    .dark{
    fill:white;
    animation: theme_icon 0.5s ease-in-out forwards reverse;
    }
    .light{
        fill:black;
        animation: theme_icon 0.5s ease-in-out forwards;
    }

@keyframes theme_icon{
    from{
        top:50px
    }
    to{
        top:10px
    }
}
</style>


